import { ByteArray } from "@project-chip/matter.js";
import assert from "assert";
import { BitByteArray, ContextTagged, DerCodec, EcdsaWithSHA256_X962, OrganisationName_X520, PublicKeyEcPrime256v1_X962 } from "../../src/codec/DerCodec";

const DECODED = {
    request: {
        version: 0,
        subject: { organization: OrganisationName_X520("CSR") },
        publicKey: PublicKeyEcPrime256v1_X962(ByteArray.fromHex("00044BD687ABD29B59D8B12E8C6614BD1664ADB2D402455B6CA3EF4E581E3BE344B83212E614F27EA4EEC8F31C75747438739B1D451A7EAB3A30542A0A7D1882A459")),
        endSignedBytes: ContextTagged(0),
    },
    signAlgorithm: EcdsaWithSHA256_X962,
    signature: BitByteArray(ByteArray.fromHex("00304602210080861AD536EFF01CAD42816A8172F71BE3E4FD7230CF73A45E34945FE89D5D7202210087FC1F47ADB6D150580706865E2E21E2963C9C15006B64DAB5658BFB980A2AD3")),
};

const ENCODED = ByteArray.fromHex("");

describe("DerCodec", () => {
    context("encode", () => {
        it("encodes a message", () => {
            const result = DerCodec.encode(DECODED);

            assert.equal(result.toHex(), ENCODED.toHex());
        });
    });
});
