import assert from "assert";
import { DerCodec, EcdsaWithSHA256_X962, ELEMENTS_KEY, DerNode, BYTES_KEY } from "../../../src/codec/DerCodec";
import { Crypto } from "../../../src/crypto/Crypto";
import { TlvRootCertificate, TlvOperationalCertificate, CertificateManager } from "../../../src/matter/certificate/CertificateManager";
import { util } from "@project-chip/matter.js";

const ROOT_CERT_TLV = TlvRootCertificate.decode(util.ByteArray.fromHex(""));
const ROOT_CERT_ASN1 = util.ByteArray.fromHex("");

const NOC_CERT_TLV = TlvOperationalCertificate.decode(util.ByteArray.fromHex(""));
const NOC_CERT_ASN1 = util.ByteArray.fromHex("");

const PRIVATE_KEY = util.ByteArray.fromHex("727F1005CBA47ED7822A9D930943621617CFD3B79D9AF528B801ECF9F1992204");
const PUBLIC_KEY = util.ByteArray.fromHex("0462e2b6e1baff8d74a6fd8216c4cb67a3363a31e691492792e61aee610261481396725ef95e142686ba98f339b0ff65bc338bec7b9e8be0bdf3b2774982476220");
const CSR_REQUEST_ASN1 = util.ByteArray.fromHex("3070020100300e310c300a060355040a0c034353523059301306072a8648ce3d020106082a8648ce3d0301070342000462e2b6e1baff8d74a6fd8216c4cb67a3363a31e691492792e61aee610261481396725ef95e142686ba98f339b0ff65bc338bec7b9e8be0bdf3b2774982476220a000");

describe("CertificateManager", () => {
    context("rootCertToAsn1", () => {
        it("generates the correct ASN1 bytes", () => {
            const result = CertificateManager.rootCertToAsn1(ROOT_CERT_TLV);

            assert.equal(result.toHex(), ROOT_CERT_ASN1.toHex());
        });
    });

    context("nocCertToAsn1", () => {
        it("generates the correct ASN1 bytes", () => {
            const result = CertificateManager.nocCertToAsn1(NOC_CERT_TLV);

            assert.equal(result.toHex(), NOC_CERT_ASN1.toHex());
        });
    });

    context("validateRootCertificate", () => {
        it("validates a correct root cert", () => {
            CertificateManager.validateRootCertificate(ROOT_CERT_TLV);
        });
    });

    context("validateNocCertificate", () => {
        it("validates a correct NOC cert", () => {
            CertificateManager.validateNocCertificate(ROOT_CERT_TLV, NOC_CERT_TLV);
        });
    });

    context("createCertificateSigningRequest", () => {
        it("generates a valid CSR", () => {
            const result = CertificateManager.createCertificateSigningRequest({ publicKey: PUBLIC_KEY, privateKey: PRIVATE_KEY });

            const derNode = DerCodec.decode(result);
            assert.equal(derNode[ELEMENTS_KEY]?.length, 3);
            const [requestNode, signatureAlgorithmNode, signatureNode] = derNode[ELEMENTS_KEY] as DerNode[];
            assert.deepEqual(DerCodec.encode(signatureAlgorithmNode), DerCodec.encode(EcdsaWithSHA256_X962));
            const requestBytes = DerCodec.encode(requestNode);
            assert.deepEqual(requestBytes, CSR_REQUEST_ASN1);
            Crypto.verify(PUBLIC_KEY, DerCodec.encode(requestNode), signatureNode[BYTES_KEY], "der");
        });
    });

    context("getPublicKeyFromCsr", () => {
        it("get the public key from the CSR", () => {
            const csr = CertificateManager.createCertificateSigningRequest({ publicKey: PUBLIC_KEY, privateKey: PRIVATE_KEY });

            const result = CertificateManager.getPublicKeyFromCsr(csr);

            assert.deepEqual(result, PUBLIC_KEY);
        });
    });
});
